insert into acciones (lugar,descripcion,fecha,status,cuerpo) values ('Lobos, Buenos Aires', 'Estuvimos en Lobos Athletico Club en lo que fue la primera acción de la Fundación.', '2021-12-10', 0, 'Estuvimos en Lobos Athletico Club en lo que fue la primera acción de la Fundación. Allí tuvo lugar un partido de fútbol ante el plantel de primera de dicha institución, sumado a viejas glorias del club. Participaron Ariel Garcé, Diego Placente, Pablo Aimar, Juan Pablo De Gregorio y Emiliano Lobo. Posterior a ello se brindó una charla a niños y niñas del club y se entregó material deportivo, tal como botines, pecheras y pelotas brindadas por Umbro.');
insert into acciones (lugar,descripcion,fecha,status,cuerpo) values ('Rosario, Santa Fe', 'Visitamos La Fabrika, allí tiene lugar un polo de cooperativas de trabajo donde se abordan valores tales como la contención, el amor y la igualdad con los más pequeños', '2022-08-18', 0, 'Visitamos La Fabrika, allí tiene lugar un polo de cooperativas de trabajo donde se abordan valores tales como la contención, el amor y la igualdad con los más pequeños, en concordancia con los valores que pregona nuestra Fundación. Conversamos con grandes y chicos. Los primeros nos contaron cómo es su cotidiano y las problemáticas que enfrentan y los jóvenes nos trasladaron su encanto respecto al avance de prácticas deportivas en el lugar, especialmente del futsal. Hicimos entrega de pelotas específicas para la actividad y recorrimos sus instalaciones donde tienen lugar emprendimientos autogestionados. Participaron de la acción Ariel Garcé y Valentín Gogorza.');
insert into acciones (lugar,descripcion,fecha,status,cuerpo) values ('Rosario, Santa Fe', 'Acompañamos al Centro Tramas, un espacio terapéutico interdisciplinario de Máximo Paz, conjuntamente con la Copa Abarth por el Autódromo Juan Manuel Fangio de Rosario', '2020-04-20', 0, 'Acompañamos al Centro Tramas, un espacio terapéutico interdisciplinario de Máximo Paz, conjuntamente con la Copa Abarth por el Autódromo Juan Manuel Fangio de Rosario. Recorrimos las instalaciones del lugar, tales como la cabina de transmisión, la calle de boxes y el podio. Los jóvenes tuvieron la posibilidad de subirse a los automóviles de la categoría, conversar con los pilotos y compartir un almuerzo y una merienda en el lugar. Participaron de la acción Ariel Garcé y Valentín Gogorza.');
insert into acciones (lugar,descripcion,fecha,status,cuerpo) values ('Concordia, Entre Ríos', 'Estuvimos en el Club Social y Deportivo La Bianca junto a Matías Chas, oriundo de la localidad y referente del club.', '2024-05-22', 0, 'Estuvimos en el Club Social y Deportivo La Bianca junto a Matías Chas, oriundo de la localidad y referente del club. Allí tuvo lugar una charla con los niños y niñas deportistas que asisten al establecimiento. Los temas que se abordaron fueron la capacidad de respuesta interna, las diferencias entre la práctica amateur y la profesional, el entrenamiento silencioso y las redes sociales digitales y su relación con el fútbol, entre otras. Hicimos entrega de botines y recorrimos sus instalaciones. Participó Ariel Garcé.');